// Disable Right Click
document.addEventListener("contextmenu", function(e){
    e.preventDefault();
},false)

// Disable Shortcut Keys
document.addEventListener("keydown",function(e){
    if(e.ctrlKey || e.keycode==123){
        e.stopPropagation();
        e.preventDefault();
    }
});

