// Money in Form
const scriptIn = 'https://script.google.com/macros/s/AKfycbxKQYZ0XXhjUX3lJd-Ks9XnTTN_ZAJ2zf546qea1u8wCc9dpeYZIW-ianwzA8Kl0tQ/exec'
const formIn = document.forms['in']

formIn.addEventListener('submit', e => {
    e.preventDefault()
    fetch(scriptIn, { method: 'POST', body: new FormData(formIn) })
        .then(response => alert("Thank you Kalyan Khatua..... Money In  our Data has Been Submitted Successfully in Our Database.....!"))
        .catch(error => console.error('Error!', error.message))
})


// Money Out Form
const scriptURL = 'https://script.google.com/macros/s/AKfycbyal8gVhQsw30V20LDPP398-qJTHhuTZnGZpUFCBZ4MGnpT-YTgK30gOLlCKbV1QMU0/exec'
const form = document.forms['out']

form.addEventListener('submit', e => {
    e.preventDefault()
    fetch(scriptURL, { method: 'POST', body: new FormData(form) })
        .then(response => alert("Thank you Kalyan Khatua..... Money Out  Your Data has Been Submitted Successfully in Our Database.....!"))
        .catch(error => console.error('Error!', error.message))
})


function view() {
    location.replace("view_details.html");
}

//  Disable Submit Button
function dis() {
    setTimeout(function () {
        document.getElementById("disableF").disabled = true;
        document.getElementById("disable").disabled = true;
    }, 500);
    setTimeout(function () {
        document.getElementById("disableF").disabled = false;
        document.getElementById("disable").disabled = false;
    }, 20000);
}


//   Date Checker and Picker Js

var date = new Date();
var tdate = date.getDate();
var mintDate = date.getDate() - 1;
var month = date.getMonth() + 1;
if (tdate < 10) {
    tdate = '0' + tdate;
}
if (mintDate < 10) {
    mintDate = '0' + mintDate;
}
if (month < 10) {
    month = '0' + month;
}
var year = date.getFullYear();

var minDate = year + '-' + month + '-' + tdate;
var lowDate = year + '-' + month + '-' + mintDate;
document.getElementById("Date-Checker-Picker").setAttribute('min', lowDate);
document.getElementById("Date-Checker-Picker").value = minDate;
document.getElementById("Date-Checker-Picker").setAttribute('max', minDate);
//    Money Out
document.getElementById("Date-Checker-Picker-out").setAttribute('min', lowDate);
document.getElementById("Date-Checker-Picker-out").value = minDate;
document.getElementById("Date-Checker-Picker-out").setAttribute('max', minDate);




let tabs = document.querySelectorAll('.tabs__toggle'),
    contents = document.querySelectorAll('.tabs__content');

tabs.forEach((tab, index) => {
    tab.addEventListener('click', () => {
        contents.forEach((content) => {
            content.classList.remove('is-active');
        });
        tabs.forEach((tab) => {
            tab.classList.remove('is-active');
        });
        contents[index].classList.add('is-active');
        tabs[index].classList.add('is-active');
    });
});